import styled from 'styled-components'
// link for react router
import {Link as LinkR} from 'react-router-dom'
// link for react scroll 
//import {Link as LinkS} from 'react-scroll'

// export becasue Nav is in another file 
// backticks or template literals for the nav element/anchor
// with media queries you can just nest it inside of it here
//margin-top: -80px; 
export const Nav = styled.nav`
background: #000;
height: 80px;
// margin-top: -80px; 
display: flex;
align-items: center;
justify-content: center;
font-size: 1rem;
position: sticky;
top: 0;
z-index: 10;

@media screen and (max-width: 960px) {
    transition: 0.8s all ease;
}

`


export const NavbarContainer = styled.div`
display: flex;
justify-content: space-between;
height: 80px;
z-index: 1;
width: 100%;
padding: 0 24px;
max-width: 1100px;
`

export const Navlogo = styled(LinkR)`
color: white;
justify-self: flex-start;
cursor: pointer;
font-size: 1.5rem;
display: flex;
align-items: center;
margin-left: 24px;
font-weight: bold;
text-decoration: none;

`
export const MobileIcon = styled.div`
display: none;

@media screen and (max-width: 760px) {
    display: block;
    position: absolute;
    top: 0;

}
`
